import react from "react"
import 'bootstrap/dist/css/bootstrap.min.css';
import {Configuration} from "openai";
import { useState } from "react";

function App() {
  const [qType , setqType] = useState("general")
  const[cbResponse,setcbresponse] = useState("");
  const[userInput,setUserInput] = useState("");
  const[isLoading,setisloading] = useState(false);

  console.log(process.env.REACT_APP_OPEN_API_KEY);
  const apiKey = process.env.REACT_APP_OPEN_API_KEY;

  return (
    <>
      hello World {apiKey ? apiKey.slice(0, 5) : "API Key Missing"}
      <input type="text" class="form-control" placeholder="Recipient's username" aria-label="Recipient's username" aria-describedby="basic-addon2"></input>
    </>
  );
}
export default App;
