// Add the following styles to your component:

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
  },
  textarea: {
    width: '100%',
    minHeight: '150px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    marginBottom: '20px',
    fontSize: '16px',
  },
  generateButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    fontSize: '18px',
    fontWeight: 'bold',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  cbResponseContainer: {
    backgroundColor: '#f7f7f7',
    padding: '20px',
    borderRadius: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    marginBottom: '20px',
  },
  likeButton: {
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  dislikeButton: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  saveButton: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  shareButton: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  continueButton: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  resetButton: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
  goToStoryButton: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  },
};

// Apply the styles to your JSX elements:

function App() {
  // ... (previous code)

  return (
    <div style={styles.container}>
      <div style={styles.heading}>Generate Stories</div>
      <div className="input-group">
        <textarea
          style={styles.textarea}
          aria-label="With textarea"
          value={userInput}
          onChange={e => setUserInput(e.target.value)}
          placeholder="Enter your story prompt"
        ></textarea>
      </div>
      <button
        type="button"
        onClick={handleEvent}
        style={styles.generateButton}
        disabled={isLoading}
      >
        {isLoading ? "Generating..." : "Generate"}
      </button>
      {cbResponse && (
        <div style={styles.cbResponseContainer}>
          <p>{cbResponse.generated_text}</p>
          <button onClick={handleLike} style={styles.likeButton}>
            Like ({likes})
          </button>
          <button onClick={handleDislike} style={styles.dislikeButton}>
            Dislike ({dislikes})
          </button>
          <div>
            <button onClick={handleSave} style={styles.saveButton}>
              Save
            </button>
            <button onClick={handleShare} style={styles.shareButton}>
              Share
            </button>
          </div>
          <button onClick={handleContinue} style={styles.continueButton}>
            Continue
          </button>
        </div>
      )}
      {userInput || cbResponse ? (
        <button onClick={handleReset} style={styles.resetButton}>
          Reset
        </button>
      ) : null}
      <Link to="/StoryList">
        <button style={styles.goToStoryButton}>Go to Story List</button>
      </Link>
    </div>
  );
}

export default App;
