// components/StoryList.js

// components/StoryList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  listItem: {
    marginBottom: '20px',
    backgroundColor: '#f7f7f7',
    padding: '10px',
    borderRadius: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  prompt: {
    fontWeight: 'bold',
  },
};

function StoryList() {
  const [stories, setStories] = useState([]);

  useEffect(() => {
    // Fetch the story data from the server
    axios.get('/api/stories').then((response) => {
      setStories(response.data);
    });
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Story Prompts and Generated Stories</h1>
      <ul>
        {stories.map((story, index) => (
          <li key={index} style={styles.listItem}>
            <h3 style={styles.prompt}>Prompt:</h3>
            <p>{story.storyPrompt}</p>
            <h3>Generated Story:</h3>
            <p>{story.generatedStory}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StoryList;
