import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-solid-svg-icons';

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  headingContainer: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px',
    marginBottom: '20px',
    borderRadius: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
  },
  listItem: {
    marginBottom: '20px',
    backgroundColor: '#f7f7f7',
    padding: '20px',
    borderRadius: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  prompt: {
    fontWeight: 'bold',
  },
  thumbsUpIcon: {
    marginRight: '5px',
  },
  thumbsDownIcon: {
    marginRight: '5px',
  },
};

function StoryList() {
  const [stories, setStories] = useState([]);

  useEffect(() => {
    // Fetch the story data from the server
    axios.get('/stories').then((response) => {
      // Sort stories by likes in descending order
      const sortedStories = response.data.sort((a, b) => b.likes - a.likes);
      setStories(sortedStories);
    });
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.headingContainer}>
        <span style={styles.heading}>AI Story Generator</span>
        {/* Add any additional buttons or elements here */}
      </div>
      <ul>
        {stories.map((story, index) => (
          <li key={index} style={styles.listItem}>
            <h3 style={styles.prompt}>Prompt:</h3>
            <p>{story.storyPrompt}</p>
            <h3>Generated Story:</h3>
            <p>{story.generatedStory}</p>
            <div>
              <button
                onClick={() => handleLike(story._id)}
                style={{ backgroundColor: '#4CAF50', color: 'white', border: 'none', padding: '10px 20px', margin: '5px', cursor: 'pointer' }}
              >
                <FontAwesomeIcon icon={faThumbsUp} style={styles.thumbsUpIcon} />
                Thumbs Up ({story.likes})
              </button>
              <button
                onClick={() => handleDislike(story._id)}
                style={{ backgroundColor: '#f44336', color: 'white', border: 'none', padding: '10px 20px', margin: '5px', cursor: 'pointer' }}
              >
                <FontAwesomeIcon icon={faThumbsDown} style={styles.thumbsDownIcon} />
                Thumbs Down ({story.dislikes})
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StoryList;
