import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import OpenAI from 'openai';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave, faShare } from "@fortawesome/free-solid-svg-icons";
import "./index.css";
import { Link } from "react-router-dom";

function App() {
  const [qType, setqType] = useState("general");
  const [cbResponse, setcbResponse] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  
  const PARAMS = {
    max_tokens: 500
  };
  const apiKey = process.env.REACT_APP_OPEN_API_KEY;
  const openai = new OpenAI({ apiKey, dangerouslyAllowBrowser: true });

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      padding: '20px',
    },
    heading: {
      fontSize: '28px',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    textarea: {
      width: '100%',
      minHeight: '150px',
      padding: '15px',
      borderRadius: '8px',
      border: '1px solid #ddd',
      marginBottom: '20px',
      fontSize: '18px',
    },
    generateButton: {
      backgroundColor: '#007bff',
      color: 'white',
      padding: '10px 20px',
      fontSize: '18px',
      fontWeight: 'bold',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    generateButtonHover: {
      backgroundColor: '#0056b3',
    },
    cbResponseContainer: {
      backgroundColor: '#f7f7f7',
      padding: '20px',
      borderRadius: '8px',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      marginBottom: '20px',
      fontSize: '18px',
    },
    likeButton: {
      backgroundColor: '#4CAF50',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    dislikeButton: {
      backgroundColor: '#f44336',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    saveShareButtonsContainer: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginTop: '20px',
    },
    saveButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    shareButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    continueResetButtonsContainer: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginTop: '20px',
    },
    continueButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    resetButton: {
      backgroundColor: '#f44336',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    goToStoryButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      textDecoration: 'none',
    },
    goToStoryButtonHover: {
      backgroundColor: '#0056b3',
    },
  };

  const handleContinue = () => {
    const continuedText = `${userInput}\n${cbResponse.generated_text}`;
    setUserInput(continuedText);
    setcbResponse("");
  };

  const handleReset = () => {
    setUserInput("");
    setcbResponse("");
  };

  const handleLike = () => {
    setLikes(likes + 1);
    setDislikes(0);
  };

  const handleDislike = () => {
    setDislikes(dislikes + 1);
    setLikes(0);
  };

  const getInstruction = async (ins) => {
    let prompt = ` ${ins}`;
    return prompt;
  }

  const handleEvent = async (e) => {
    e.preventDefault();
    const prompt = await getInstruction(userInput);
    setLikes(0);
    setDislikes(0);

    try {
      setIsLoading(true);
      const response = await fetch("/GPT", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ data: prompt })
      });
      const data = await response.json();
      setcbResponse(data[0]);
    } catch (error) {
      console.error('API Error:', error);
    } finally {
      setIsLoading(false);
    }
  }

  const handleSave = () => {
    const blob = new Blob([cbResponse.generated_text], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_text.txt";
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: "Generated Text",
        text: cbResponse.generated_text,
      })
      .then(() => console.log("Shared successfully"))
      .catch((error) => console.error("Share failed:", error));
    } else {
      alert("Sharing is not supported in this browser.");
    }
  };

  return (
    <div className="container">
      <h1 className="my-4">Advanced AI Story Generator</h1>
      <textarea
        className="form-control my-3"
        value={userInput}
        onChange={e => setUserInput(e.target.value)}
        placeholder="Enter your story prompt"
      />
      <button
        type="button"
        onClick={handleEvent}
        className={`btn btn-primary ${isLoading ? 'disabled' : ''}`}
      >
        {isLoading ? "Generating..." : "Generate"}
      </button>

      {cbResponse && (
        <div className="mt-4">
          <div className="alert alert-secondary">
            <p>{cbResponse.generated_text}</p>
          </div>
          <div className="d-flex justify-content-start align-items-center mt-3">
            <button
              onClick={handleLike}
              className="btn btn-success"
            >
              Like ({likes})
            </button>
            <button
              onClick={handleDislike}
              className="btn btn-danger ml-2"
            >
              Dislike ({dislikes})
            </button>
          </div>
          <div className="mt-3">
            <button
              onClick={handleSave}
              className={`btn btn-primary ${likes > 0 ? 'disabled' : ''}`}
            >
              Save
            </button>
            <button
              onClick={handleShare}
              className={`btn btn-primary ${dislikes > 0 ? 'disabled' : ''}`}
            >
              Share
            </button>
          </div>
          <button
            onClick={handleContinue}
            className="btn btn-primary mt-3"
          >
            Continue
          </button>
        </div>
      )}
      {userInput || cbResponse ? (
        <button
          onClick={handleReset}
          className="btn btn-danger mt-4"
        >
          Reset
        </button>
      ) : null}
      <Link to="/StoryList" className="btn btn-primary mt-4">
        Go to Story List
      </Link>
    </div>
  );
}

export default App;
