import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import OpenAI from 'openai';

function App() {
  const [qType, setqType] = useState("general");
  const [cbResponse, setcbResponse] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const PARAMS = {
    max_tokens: 256
  };
  const apiKey = process.env.REACT_APP_OPEN_API_KEY;
  const openai = new OpenAI({ apiKey, dangerouslyAllowBrowser: true });

  const getInstruction = async (ins) => {
    let prompt = `generate a full story on the basis of the given story prompt: ${ins}`;
    return prompt;
  }

  const handleEvent = async (e) => {
    e.preventDefault();
    const prompt = await getInstruction(userInput);
    //const endpoint = 'https://api.openai.com/v1/engines/davinci-codex/completions';
    const body = { ...PARAMS, prompt };

    try {
      setIsLoading(true);
      const response = await fetch("?GET", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify(body)
      });
      const data = await response.json();
      setcbResponse(data.choices[0].text);
    } catch (error) {
      console.error('API Error:', error);
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <>
      <div>
        hello World {apiKey ? apiKey.slice(0, 5) : "API Key Missing"}
      </div>
      <div className="input-group">
        <textarea
          className="form-control my-3"
          aria-label="With textarea"
          value={userInput}
          onChange={e => setUserInput(e.target.value)}
          placeholder="enter your story prompt"
        ></textarea>
      </div>
      <button
        type="button"
        onClick={handleEvent}
        className="btn btn-primary"
        disabled={isLoading}
      >
        {isLoading ? "Generating..." : "Generate"}
      </button>
      {cbResponse && <div>{cbResponse}</div>}
    </>
  );
}

export default App;
