import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import OpenAI from 'openai';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave, faShare } from "@fortawesome/free-solid-svg-icons";
import "./index.css";

function App() {
  const [qType, setqType] = useState("general");
  const [cbResponse, setcbResponse] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [likes, setLikes] = useState(0);
  
  const [dislikes, setDislikes] = useState(0);
  const PARAMS = {
    max_tokens: 500
  };
  const apiKey = process.env.REACT_APP_OPEN_API_KEY;
  const openai = new OpenAI({ apiKey, dangerouslyAllowBrowser: true });
  const handleLike = () => {
    setLikes( 1);
    setDislikes(0);
  };

  const handleDislike = () => {
    setDislikes( 1);
    setLikes(0);
  };

  const getInstruction = async (ins) => {
    let prompt = ` ${ins}`;
    return prompt;
  }


  const handleEvent = async (e) => {
    e.preventDefault();
    const prompt = await getInstruction(userInput);
    console.log(userInput);
    setLikes(0);
  setDislikes(0);
    //const endpoint = 'https://api.openai.com/v1/engines/davinci-codex/completions';
    const body = { ...PARAMS, prompt };
    console.log(body)

    try {
      setIsLoading(true);
      const response = await fetch("/GPT", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json',
          
        },
        body: JSON.stringify({data:prompt})
      });
      const data = await response.json();
      console.log(data[0])
      
      setcbResponse(data[0]);
      
     
    } catch (error) {
      console.error('API Error:', error);
    } finally {
      setIsLoading(false);
    }
  }
  const handleSave = () => {
    // Implement save functionality here
    const blob = new Blob([cbResponse.generated_text], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_text.txt";
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleShare = () => {
    // Implement share functionality here
    if (navigator.share) {
      navigator.share({
        title: "Generated Text",
        text: cbResponse.generated_text,
      })
      .then(() => console.log("Shared successfully"))
      .catch((error) => console.error("Share failed:", error));
    } else {
      alert("Sharing is not supported in this browser.");
    }
  };


  return (
    <>
      <div>
        hello World {apiKey ? apiKey.slice(0, 5) : "API Key Missing"}
      </div>
      <div className="input-group">
        <textarea
          className="form-control my-3"
          aria-label="With textarea"
          value={userInput}
          onChange={e => setUserInput(e.target.value)}
          placeholder="enter your story prompt"
        ></textarea>
      </div>
     

<button
  type="button"
  onClick={handleEvent}
  className="btn btn-primary"
  disabled={isLoading}
  style={{ marginBottom: "20px" }} // Add margin to the button
>
  {isLoading ? "Generating..." : "Generate"}
</button>



{/* {cbResponse && (
  <div className="cbResponseContainer">
    <p>{cbResponse.generated_text}</p>
    <button
      onClick={handleLike}
      style={{
        backgroundColor: "#4CAF50", // Green background color
        color: "white",             // White text color
        border: "none",            // No border
        padding: "10px 20px",       // Padding
        margin: "5px",              // Margin
        cursor: "pointer",         // Cursor style
      }}
    >
      Like ({likes})
    </button>
    <button
      onClick={handleDislike}
      style={{
        backgroundColor: "#f44336", // Red background color
        color: "white",             // White text color
        border: "none",            // No border
        padding: "10px 20px",       // Padding
        margin: "5px",              // Margin
        cursor: "pointer",         // Cursor style
      }}
    >
      Dislike ({dislikes})
    </button>
  </div>
)} */}
{/* {cbResponse && (
      <div className="cbResponseContainer">
        <p>{cbResponse.generated_text}</p>
        <button
          onClick={handleLike}
          style={{
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            padding: "10px 20px",
            margin: "5px",
            cursor: "pointer",
          }}
        >
          Like ({likes})
        </button>
        <button
          onClick={handleDislike}
          style={{
            backgroundColor: "#f44336",
            color: "white",
            border: "none",
            padding: "10px 20px",
            margin: "5px",
            cursor: "pointer",
          }}
        >
          Dislike ({dislikes})
        </button>
        <div>
          <button onClick={handleSave}>Save</button>
          <button onClick={handleShare}>Share</button>
        </div>
      </div>
    )} */}
    {cbResponse && (
        <div className="cbResponseContainer">
          <p>{cbResponse.generated_text}</p>
          <button
            onClick={handleLike}
            style={{
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              padding: "10px 20px",
              margin: "5px",
              cursor: "pointer",
            }}
          >
            Like ({likes})
          </button>
          <button
            onClick={handleDislike}
            style={{
              backgroundColor: "#f44336",
              color: "white",
              border: "none",
              padding: "10px 20px",
              margin: "5px",
              cursor: "pointer",
            }}
          >
            Dislike ({dislikes})
          </button>
          <div>
            {/* Use Font Awesome icons for save and share */}
            <FontAwesomeIcon
              icon={faSave}
              size="2x"
              style={{ cursor: "pointer", marginRight: "10px" }}
              onClick={handleSave}
            />
            <FontAwesomeIcon
              icon={faShare}
              size="2x"
              style={{ cursor: "pointer" }}
              onClick={handleShare}
            />
          </div>
        </div>
      )}









     

    </>
  );
}

export default App;
