import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import OpenAI from 'openai';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave, faShare } from "@fortawesome/free-solid-svg-icons";
import "./index.css";
import { Link } from "react-router-dom";

function App() {
  const [qType, setqType] = useState("general");
  const [cbResponse, setcbResponse] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  
  const PARAMS = {
    max_tokens: 500
  };
  const apiKey = process.env.REACT_APP_OPEN_API_KEY;
  const openai = new OpenAI({ apiKey, dangerouslyAllowBrowser: true });

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      padding: '20px',
    },
    heading: {
      fontSize: '24px',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    textarea: {
      width: '100%',
      minHeight: '150px',
      padding: '10px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      marginBottom: '20px',
      fontSize: '16px',
    },
    generateButton: {
      backgroundColor: '#007bff',
      color: 'white',
      padding: '10px 20px',
      fontSize: '18px',
      fontWeight: 'bold',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
    cbResponseContainer: {
      backgroundColor: '#f7f7f7',
      padding: '20px',
      borderRadius: '5px',
      boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
      marginBottom: '20px',
    },
    likeButton: {
      backgroundColor: '#4CAF50',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
    dislikeButton: {
      backgroundColor: '#f44336',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      marginLeft: '100px',
      cursor: 'pointer',
    },
    saveButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
    shareButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
    continueButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
    resetButton: {
      backgroundColor: '#f44336',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
    goToStoryButton: {
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      margin: '5px',
      cursor: 'pointer',
    },
  };

  const handleContinue = () => {
    // Append the generated text to the existing user input
    const continuedText = `${userInput}\n${cbResponse.generated_text}`;
    setUserInput(continuedText);
    // Optionally, you can clear the generated response if needed
    setcbResponse("");
  };

  const handleReset = () => {
    // Clear the text input and the generated response
    setUserInput("");
    setcbResponse("");
  };

  const handleLike = () => {
    setLikes(1);
    setDislikes(0);
  };

  const handleDislike = () => {
    setDislikes(1);
    setLikes(0);
  };

  const getInstruction = async (ins) => {
    let prompt = ` ${ins}`;
    return prompt;
  }

  const handleEvent = async (e) => {
    e.preventDefault();
    const prompt = await getInstruction(userInput);
    console.log(userInput);
    setLikes(0);
    setDislikes(0);

    const body = { ...PARAMS, prompt };
    console.log(body)

    try {
      setIsLoading(true);
      const response = await fetch("/GPT", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({data:prompt})
      });
      const data = await response.json();
      console.log(data[0])
      setcbResponse(data[0]);
     
    } catch (error) {
      console.error('API Error:', error);
    } finally {
      setIsLoading(false);
    }
  }

  const handleSave = () => {
    // Implement save functionality here
    const blob = new Blob([cbResponse.generated_text], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_text.txt";
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleShare = () => {
    // Implement share functionality here
    if (navigator.share) {
      navigator.share({
        title: "Generated Text",
        text: cbResponse.generated_text,
      })
      .then(() => console.log("Shared successfully"))
      .catch((error) => console.error("Share failed:", error));
    } else {
      alert("Sharing is not supported in this browser.");
    }
  };

  // ...
return (
  <div className="container-fluid" style={{ backgroundColor: "#f5f5f5", minHeight: "100vh", padding: "20px" }}>
    <h1 className="my-4">Advanced AI Story Generator</h1>
    <textarea
      className="form-control my-3"
      value={userInput}
      onChange={(e) => setUserInput(e.target.value)}
      placeholder="Enter your story prompt"
    />
    <button
      type="button"
      onClick={handleEvent}
      className={`btn btn-primary ${isLoading ? "disabled" : ""}`}
      style={{ fontSize: "16px" }} // Adjust button font size
    >
      {isLoading ? "Generating..." : "Generate"}
    </button>

    {cbResponse && (
      <div className="mt-4">
        <div className="alert alert-secondary">
          <p>{cbResponse.generated_text}</p>
        </div>
        <div className="d-flex justify-content-between align-items-center mt-3">
          <div>
            <button
              onClick={handleLike}
              className="btn btn-success"
              style={{ fontSize: "16px" }} // Adjust button font size
            >
              Like ({likes})
            </button>
          </div>
          <div>
            <button
              onClick={handleDislike}
              className="btn btn-danger"
              style={{ fontSize: "16px" }} // Adjust button font size
            >
              Dislike ({dislikes})
            </button>
          </div>
        </div>
        <div className="mt-3">
          <button
            onClick={handleSave}
            className={`btn btn-primary ${likes > 0 ? "disabled" : ""}`}
            style={{ fontSize: "16px" }} // Adjust button font size
          >
            Save
          </button>
          <button
            onClick={handleShare}
            className={`btn btn-primary ${dislikes > 0 ? "disabled" : ""}`}
            style={{ fontSize: "16px" }} // Adjust button font size
          >
            Share
          </button>
        </div>
        <button
          onClick={handleContinue}
          className="btn btn-primary mt-3"
          style={{ fontSize: "16px" }} // Adjust button font size
        >
          Continue
        </button>
      </div>
    )}
    {userInput || cbResponse ? (
      <button
        onClick={handleReset}
        className="btn btn-danger mt-4"
        style={{ fontSize: "16px" }} // Adjust button font size
      >
        Reset
      </button>
    ) : null}
    <Link
      to="/StoryList"
      className="btn btn-primary mt-4"
      style={{ fontSize: "16px" }}
    >
      Go to Story List
    </Link>
  </div>
);
// ...

}

export default App;
